mapper.update:
update article set status = #{status},update_time = #{updateTime}
where id in

    #{id}

mapper.select:
select count(*)
from article

    status != 1

        and user_id = #{userId}


        and title LIKE CONCAT('%',#{title},'%')


        and top = #{top}


        and marrow = #{marrow}

select 
from article

    status != 1

        and user_id = #{userId}


        and title LIKE CONCAT('%',#{title},'%')


        and top = #{top}


        and marrow = #{marrow}


order by create_time desc
limit #{offset}, #{limit}
